import React from 'react'

const signup = () => {
  const [home, setHome] = useState(false);
function toogle() {
  if (useState(false)) {
    <Signin/>
  } else {
    <Signup/>
  }
}

  return (
    <>
    <div className="grid h-screen signUp place-items-center ">
        
        <div className="grid px-12 py-20 border place-items-center">

          <img className="max-w-52 " src={require("./insta.png")} />

          <form className="flex flex-col gap-1 py-5 text-xs w-60">
            <label htmlFor="mobile-number"></label>
            <input className="px-2 py-3 border rounded" placeholder="Phone number or Email" type="text" />
            <label htmlFor="full-name"></label>
            <input className="px-2 py-3 border rounded" placeholder="Full Name" type="text" />
            <label htmlFor="email"></label>
            <input className="px-2 py-3 border rounded" placeholder="Email Address" type="email" />
            <label htmlFor="password"></label>
            <input className="px-2 py-3 border rounded" placeholder="Password" type="password" />
            <button className="w-full py-2 mt-4 font-semibold text-white rounded-md bg-sky-500">Sign In</button>
          </form>

        <span>Don't have account? <a onClick={toogle()} className="font-semibold text-sky-600" href="">Sign up</a></span>
       
        </div>



      </div>
    </>
  )
}

export default signup
